import React, { useState, useEffect } from "react";
import Cookies from 'js-cookie'
import jwt_decode from "jwt-decode";
import Message from 'components/Message/Message';
import { useForm } from "react-hook-form";
import './Home.scss';

const Home = () => {
  	const [displayError, setDisplayError] = useState('');
  	const [messages, setMessages] = useState('');
    const { register, handleSubmit, watch, errors } = useForm();
    const id = useSelector(state => state.id);

  	const loadPosts = data => {
  		fetch('http://localhost:1337/posts', {
  		  method: 'get',
  		  headers: {
    		'Content-Type': 'application/json'
  		  }
		})
		.then((response) => response.json())
		.then((response) => setMessages(response))
		.catch((error) => setDisplayError('Pas authentifié'));
  	}

    const createPost = data => {
      fetch('http://localhost:1337/posts', {
        method: 'post',
        headers: {
        'Authorization': `Bearer ${Cookies.get('token')}`,
        'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
    })
    .then((response) => response.json())
    .then((response) => console.log(response))
    .catch((error) => setDisplayError('Pas authentifié'));
    }

  	useEffect(() => {
    loadPosts();
  	}, [])

	return (
		<div className="Home">
    		<p>Welcome on My Social Network. This website is a training to Redux and React. We use auth and routing to create a small social media website.</p>
        <p>nouveau message</p>

        <form onSubmit={handleSubmit(createPost)} className="Register">
          <input name="username" type="text" value={id} ref={register({ required: true })} />
          <input name="text" type="text" placeholder="nouveau message" ref={register({ required: true })} />
          <input type="submit" />
        <p>{displayError}</p>
        </form>

      	<ul className='Home__messages'>
      	{messages && messages.map((message) => (
      		<Message message={message}/>
      	))}
      	</ul>
        <p>{displayError}</p>
    </div>
	);
};

export default Home;
